def get_park_multiplier(park_name: str, prop_type: str) -> float:
    prop_key = prop_type.lower()
    for park, factors in BALLPARK_FACTORS.items():
        if park_name.lower() in park.lower():
            return factors.get(prop_key, 1.0)
    return 1.0  # Default multiplier if unknown
